import React from 'react';
import "bootstrap/dist/css/bootstrap.css"
import CardList from './CardList.js';
import FilterForm from './Filter.js';
import SourceData from './Sourcedata.js';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false
    }
  }

  componentDidMount() {
    /**
    fetch('https://citz.sp.gov.bc.ca/sites/uat/_api/search/query?querytext='contentclass: sts_List_ AND ParentLink: "https://citz.sp.gov.bc.ca/sites/UAT" '&rowlimit=500')
      .then(res => res.json())
      .then(json => {
        this.setState({
          isLoaded: true,
          items: json,
        })
      });
       */
  }

  render() {
    var { isLoaded, items } = this.state;

    if (!isLoaded) {
      return <div>Loading...</div>;
    } else {

      return (
        <div id="csp_srchlists">
          <div className="container-fluid">
            <h1>Lists and Libraries</h1>
            <FilterForm />
            <CardList />
          </div>
        </div>
      );
    }
  }
}

export default App;
